import React from "react";
import "./Card.scss";
import spades from "../../images/spade.png"

const Card = (props) => {
  const { CardArr } = props;

  const Cards = CardArr.map((card, index) => {


    if (card.suit=== "spades"){
      return (
        <div key={index} className="card">
          <h1>{card.value}</h1>
          <img class= "card__suit" src={spades}/>
        </div>
      );
    }

  });

  return <div className="Cards">{Cards}</div>;
};

export default Card;
