import React from "react";
import "./Card.scss";
import spades from "../../images/spade.png";
import diamonds from "../../images/diamond.png";
import clubs from "../../images/club.png";
import hearts from "../../images/heart.png";

const Card = (props) => {
  const { CardArr } = props;

  const Cards = CardArr.map((card, index) => {

    if (card.suit=== "spades"){
      return (
        <div key={index} className="card">
          <h1>{card.value}</h1>
          <img class= "card__suit" src={spades} alt="spades"/>
        </div>
      );
    }

    if (card.suit=== "diamonds"){
      return (
        <div key={index} className="card">
          <h1>{card.value}</h1>
          <img class= "card__suit" src={diamonds}/>
        </div>
      );
    }

    if (card.suit=== "clubs"){
      return (
        <div key={index} className="card">
          <h1>{card.value}</h1>
          <img class= "card__suit" src={clubs}/>
        </div>
      );
    }

    if (card.suit=== "hearts"){
      return (
        <div key={card.Card} className="card">
          <h1>{card.value}</h1>
          <img class= "card__suit" src={hearts}/>
        </div>
      );
    }
  });

  return <div className="Cards">{Cards}</div>;
};

export default Card;
