import React, { useState } from "react";
import "./ArtistTile.scss";
import Button from "../Button/Button";
import cross from "../../assets/images/white-cross.png";

const DiscoverArtist = (props) => {
  const { imgSrc, title, text } = props;
  const [showtext, SetShowBio] = useState(false);

  const handleClick = () => {
    SetShowBio(!showtext);
  };

  return (
    <div className="artist-tile">
      <img src={imgSrc} className="artist-tile__img" alt={title} />
      <div className="artist-tile__content artist-tile__content--button">
        <h3 className="artist-tile__heading">{title}</h3>
        <div onClick={handleClick}>
          {showtext !== true && (
            <Button buttonText={"Find out more"} isSecondary={true} />
          )}
          {showtext && (
            <div className="artist-tile--text">
              <img src={cross} className="artist-tile__cross" alt="cross" />
              {text.split(".").map(sentence => (
      <p>{sentence + "."}</p>
    ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DiscoverArtist;
