import "./App.scss";
import sunrise from "./assets/images/sunrise.png";
import sun from "./assets/images/sun.png";
import moon from "./assets/images/moon.png";
import Nav from "./components/Nav/Nav";
import Button from "./components/Button/Button";
import artist from "./data/artist";
import ArtistTile from "./components/ArtistTile/ArtistTile";

const App = () => {
  const user = {
    firstName: "John",
    lastName: "Doe",
  };

  const currentHour = new Date().getHours();
  let greetingImg = sunrise;
  let greetingTime = "Morning!";

  if (currentHour >= 12) {
    greetingImg = sun;
    greetingTime = "Afternoon!";
  }

  if (currentHour >= 18) {
    greetingImg = moon;
    greetingTime = "Evening!";
  }

  const team = [
    {name: "alin", score:80},
    {name:"ami", score:120},
    {name:"jasen", score:40},
  ]

const filterByScore = team.filter(player => {
  return player.score>100  //true or false
})

const teamJSX = filterByScore.map(player =>{
  return <h2>I am {player.name} and I scored {player.score}, I am at {index}</h2>
})

  return (
    <>
    {teamJSX}
      <div className="app">
        <Nav />
        <header className="greeting">
          <img src={greetingImg} className="greeting__img" alt={greetingTime} />
          <h1 className="greeting__heading">
            Good {greetingTime} <br /> {user.firstName} {user.lastName}
          </h1>
        </header>
        <section className="button-section">
          <Button buttonText={"Lets Go"} isSecondary={true} />
          <Button buttonText={"Explore"} />
        </section>
        <section className="discover">
          <h2>Discover</h2>
          <ArtistTile imgSrc={artist.strArtistThumb} title={artist.strArtist} />
        </section>
      </div>
    </>
  );
};

export default App;
