// ArtistTile.jsx
import React from "react";
import "./ArtistTile.scss";

const ArtistTile = (props) => {
    let artistStyle = "artisttile ";

    if(props.isSecondary===true){
      buttonStyle += "artist--secondary";
    }else {
      buttonStyle += "artist--primary"
    }

    return <div className={artistStyle}>{props.artistText}</div>;
  };

  export default ArtistTile;
