// ArtistTile.jsx
import React from "react";
import "./ArtistTile.scss";
import Button from "./components/button/Button";

const ArtistTile = (props) => {
    let artistStyle = "artisttile ";

    if(props.isSecondary===true){
      buttonStyle += "artist--secondary";
    }else {
      buttonStyle += "artist--primary"
    }

    return <div className={artistStyle}>{props.artistText}
    <button className={buttonStyle}>{props.buttonText}</button>
    </div>;
  };

  export default ArtistTile;
