import React, { useState } from "react";
import menu from "../../assets/images/menu-icon.png";
import settings from "../../assets/images/settings-icon.png";
import "./Nav.scss";
import SettingsMenu from "../../components/SettingsMenu/SettingsMenu";
import NavMenu from "../../components/NavMenu/NavMenu";

const Nav = (props) => {
  const { userName } = props;
  const [showSettings, setShowSettngs] = useState(false);
  const [showNavMenu, setShowNavMenu] = useState(true);

  const toggleSettings = () => {
    setShowSettngs(!showSettings);
  };

  const toggleMenu = () => {
    setShowNavMenu(!showNavMenu);
  };

  return (
    <nav className="nav">
      {showSettings && (
        <SettingsMenu userName={userName} toggleSettings={toggleSettings} />
      )}
      {showNavMenu && <NavMenu toggleNav={toggleSettings} />}
      <img
        onClick={toggleMenu}
        src={menu}
        className="nav__item "
        alt="menu icon"
      />
      <h2 className="nav__heading">Ear Worm</h2>
      <img
        onClick={toggleSettings}
        src={settings}
        className="nav__item"
        alt="settings icon"
      />
    </nav>
  );
};

export default Nav;
