import "./App.scss";
import moon from "./assets/images/moon.png";
import sun from "./assets/images/sun.png";
import sunrise from "./assets/images/sunrise.png";

const App = () => {
  /*
  //testing display
  let message = "We can do JS here"
  if (message.length > 10) {
    message += ", We can add logic here too.";
  }
  */

  //get name
  const user = {
    firstName: "Joe",
    lastName: "Doe",
  }

  //get current date and hour
  const currentdate = new Date().getHours();
  console.log(currentdate);

  let greetingImage = sunrise;
  let greetingMessage = "Good Morning!";

  if (currentdate >11) {
    greetingImage = sun;
    greetingMessage = "Good Afternoon!";
  }

  if (currentdate >18) {
    greetingImage = moon;
    greetingMessage = "Good Evening!";
  }

  return (
    <div className="app">
      <header className="greeting">
        <h1 className="greeting__heading">Hello World</h1>
        <img src = {greetingImage} alt = "greeting-icon"/>
        <p>{greetingMessage}  {user.firstName} {user.lastName}</p>
      </header>
    </div>
  );
};

export default App;
