import React, { useState } from "react";
import menu from "../../assets/images/menu-icon.png";
import settings from "../../assets/images/settings-icon.png";
import "./Nav.scss";
import SettingsMenu from "../../components/SettingsMenu/SettingsMenu";

const Nav = (props) => {
  const { userName } = props;
  const [showSettings, setShowSettngs] = useState(false);

  const toggleSettings = () => {
    setShowSettngs(!showSettings);
  };

  return (
    <nav className="nav">
      {showSettings && (
        <SettingsMenu userName={userName} toggleSettings={toggleSettings} />
      )}
      <img src={menu} className="nav__item " alt="" />
      <h2 className="nav__heading">Ear Worm</h2>
      <img
        onClick={toggleSettings}
        src={settings}
        className="nav__item"
        alt=""
      />
    </nav>
  );
};

export default Nav;
